import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from './Button';
import api from '../services/api';

// Estilo do formulário principal
const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
`;

// Container dos campos
const FieldsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  margin-left: -3.5rem; // Alinha com a aba
`;

// Campos de entrada com largura fixa
const Input = styled.input`
  height: 38px;
  width: 340px;
  padding: 0 1rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: 6px;
`;

// Select com mesma largura dos inputs
const Select = styled(Input).attrs({ as: 'select' })``;

// Grid para os campos técnicos
const TechnicalFields = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 340px);
  gap: 2rem;
`;

// Container para os campos de horário
const TimeContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  input {
    width: 140px;
  }
`;

const ClienteForm = () => {
  const [formData, setFormData] = useState({
    consumoMensal: '',
    demandaContratada: '',
    tensaoFornecimento: '',
    classeConsumidor: '',
    modalidadeTarifaria: '',
    diasFuncionamento: '',
    horarioFuncionamento: { inicio: '', fim: '' }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Permite busca com campos parcialmente preenchidos
    const filteredData = Object.fromEntries(
      Object.entries(formData).filter(([_, value]) => value !== '')
    );

    try {
      const response = await api.post('/buscar-fornecedores', filteredData);
      // Atualizar estado com os resultados
      setResultados(response.data);
    } catch (error) {
      console.error('Erro ao buscar fornecedores:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <Form onSubmit={handleSubmit}>
      <FieldsContainer>
        {/* Dados de Consumo */}
        <div>
          <h3>Dados de Consumo</h3>
          <Input type="number" placeholder="Ex: 30000" />
          <Input type="number" placeholder="Ex: 100" />
        </div>

        {/* Dados Técnicos */}
        <TechnicalFields>
          <Select>
            <option>Baixa Tensão (BT)</option>
          </Select>
          <Select>
            <option>Comercial</option>
          </Select>
          <Select>
            <option>Convencional</option>
          </Select>
        </TechnicalFields>

        {/* Perfil de Consumo */}
        <div>
          <Select>
            <option>Segunda a Sexta</option>
          </Select>
          <TimeContainer>
            <Input type="time" />
            <span>até</span>
            <Input type="time" />
          </TimeContainer>
        </div>

        <button type="submit">Buscar</button>
      </FieldsContainer>
    </Form>
  );
};

export default ClienteForm;
