import React from 'react';
import styled from 'styled-components';
import { BsFilter, BsBarChart, BsListCheck, BsGrid } from 'react-icons/bs';

const BuscaToolbar = ({ 
  onToggleFiltros, 
  onToggleView, 
  viewMode,
  totalFornecedores,
  filtrosAtivos 
}) => (
  <Container>
    <LeftSection>
      <ToolbarButton 
        onClick={onToggleFiltros}
        $active={filtrosAtivos > 0}
      >
        <BsFilter />
        Filtros {filtrosAtivos > 0 && `(${filtrosAtivos})`}
      </ToolbarButton>
      
      <Separator />
      
      <ViewToggle>
        <ViewButton 
          onClick={() => onToggleView('grid')}
          $active={viewMode === 'grid'}
        >
          <BsGrid /> Grid
        </ViewButton>
        <ViewButton 
          onClick={() => onToggleView('list')}
          $active={viewMode === 'list'}
        >
          <BsListCheck /> Lista
        </ViewButton>
        <ViewButton 
          onClick={() => onToggleView('chart')}
          $active={viewMode === 'chart'}
        >
          <BsBarChart /> Comparação
        </ViewButton>
      </ViewToggle>
    </LeftSection>

    <RightSection>
      <ResultCount>
        {totalFornecedores} fornecedores encontrados
      </ResultCount>
    </RightSection>
  </Container>
);

export default BuscaToolbar;

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  margin-bottom: 1rem;
`;

const LeftSection = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const ToolbarButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : 'transparent'};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  cursor: pointer;
  transition: all 0.2s ease;
`;

const Separator = styled.div`
  width: 1px;
  height: 24px;
  background: ${({ theme }) => theme.colors.border};
`;

const ViewToggle = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ViewButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : 'transparent'};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  cursor: pointer;
  transition: all 0.2s ease;
`;

const ResultCount = styled.span`
  color: ${({ theme }) => theme.colors.text.secondary};
  font-size: 0.875rem;
`;

// ... outros styled components
