import React, { useState } from 'react';
import {
  StyledForm,
  FieldGroup,
  FieldLabel,
  FieldDescription,
  BaseInput,
  Select,
  TechnicalGrid,
  TabNavigation,
  Tab,
  FormLayout,
  FormSection,
  InputGroup,
  FormActions,
  SubmitButton
} from './styles';
import api from '../../services/api';

const ClienteForm = () => {
  const [formData, setFormData] = useState({
    consumoMensal: '',
    demandaContratada: '',
    tensaoFornecimento: '',
    classeConsumidor: '',
    modalidadeTarifaria: '',
    diasFuncionamento: '',
    horarioFuncionamento: { inicio: '', fim: '' }
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/buscar-fornecedores', formData);
    } catch (error) {
      console.error('Erro:', error);
    }
  };

  return (
    <StyledForm onSubmit={handleSubmit}>
      <FormLayout>
        <FormSection>
          <FieldGroup>
            <FieldLabel>Consumo Mensal (kWh)</FieldLabel>
            <BaseInput
              type="number"
              name="consumoMensal"
              value={formData.consumoMensal}
              onChange={handleInputChange}
              placeholder="Ex: 30000"
            />
            <FieldDescription>Média dos últimos 12 meses</FieldDescription>
          </FieldGroup>

          <FieldGroup>
            <FieldLabel>Demanda Contratada (kW)</FieldLabel>
            <BaseInput
              type="number"
              name="demandaContratada"
              value={formData.demandaContratada}
              onChange={handleInputChange}
              placeholder="Ex: 100"
            />
            <FieldDescription>Potência contratada atual</FieldDescription>
          </FieldGroup>
        </FormSection>

        <TechnicalGrid>
          <FieldGroup>
            <FieldLabel>Tensão de Fornecimento</FieldLabel>
            <Select
              name="tensaoFornecimento"
              value={formData.tensaoFornecimento}
              onChange={handleInputChange}
            >
              <option value="baixa">Baixa Tensão (BT)</option>
            </Select>
          </FieldGroup>

          <FieldGroup>
            <FieldLabel>Classe do Consumidor</FieldLabel>
            <Select
              name="classeConsumidor"
              value={formData.classeConsumidor}
              onChange={handleInputChange}
            >
              <option value="comercial">Comercial</option>
            </Select>
          </FieldGroup>

          <FieldGroup>
            <FieldLabel>Modalidade Tarifária</FieldLabel>
            <Select
              name="modalidadeTarifaria"
              value={formData.modalidadeTarifaria}
              onChange={handleInputChange}
            >
              <option value="convencional">Convencional</option>
            </Select>
          </FieldGroup>
        </TechnicalGrid>

        <FormActions>
          <SubmitButton type="submit">Buscar</SubmitButton>
        </FormActions>
      </FormLayout>
    </StyledForm>
  );
};

export default ClienteForm;
