import React from 'react';
import styled from 'styled-components';
import { 
  SearchIcon,
  ChartIcon,
  FileIcon,
  GearIcon
} from './icons';

const tabs = [
  { id: 'busca', icon: SearchIcon, label: 'Buscar Fornecedores' },
  { id: 'economia', icon: ChartIcon, label: 'Análise de Economia' },
  { id: 'comparacao', icon: FileIcon, label: 'Comparar Fornecedores' },
  { id: 'configuracoes', icon: GearIcon, label: 'Configurações' }
];

const TabNavigation = ({ activeTab, onTabChange }) => (
  <Container>
    {tabs.map(tab => {
      const IconComponent = tab.icon;
      return (
        <TabButton
          key={tab.id}
          $active={activeTab === tab.id}
          onClick={() => onTabChange(tab.id)}
        >
          <IconComponent />
          <span>{tab.label}</span>
        </TabButton>
      );
    })}
  </Container>
);

const Container = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 2rem;
  background: ${({ theme }) => theme.colors.background.card};
  padding: 0.5rem;
  border-radius: ${({ theme }) => theme.borderRadius.lg};
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : 'transparent'};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${({ $active, theme }) => 
      $active ? theme.colors.primary.main : theme.colors.background.hover};
  }
`;

export default TabNavigation;
