  gap: 0.5rem;
`;

const Switch = styled.input.attrs({ type: 'checkbox' })`
  position: relative;
  width: 40px;
  height: 20px;
  appearance: none;
  background: ${({ theme }) => theme.colors.border};
  border-radius: 10px;
  cursor: pointer;
  transition: all 0.3s;

  &:checked {
    background: ${({ theme }) => theme.colors.primary.main};
  }

  &:before {
    content: '';
    position: absolute;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    top: 2px;
    left: 2px;
    background: white;
    transition: all 0.3s;
  }

  &:checked:before {
    transform: translateX(20px);
  }
`;

const SwitchLabel = styled.span`
