import styled from 'styled-components';

export const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
  margin: 0;
`;

// Layout principal com duas colunas
export const FormLayout = styled.div`
  display: grid;
  grid-template-columns: 400px minmax(600px, 1fr); // Voltando ao tamanho original
  gap: 4rem; // Voltando ao gap original
  margin-top: 2rem;
  padding: 0 2rem;
  width: 100%;
`;

// Container para a lista/grid de fornecedores
export const FornecedoresContainer = styled.div`
  margin-top: 2rem;
  width: calc(100vw - 1400px);
  margin-left: 50px;
  padding: 0;
  position: relative;

  // Ajuste para a tabela
  table {
    width: 100%;
    max-width: 100%;
    background: white;
  }
`;

// Título das seções com linha verde
export const SectionTitle = styled.h2`
  font-size: 1.25rem;
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 600;
  margin-bottom: 2rem;
  padding-bottom: 0.75rem;
  border-bottom: 2px solid ${({ theme }) => theme.colors.primary.main};
  width: 100%;
`;

// Seção técnica mais compacta verticalmente
export const FormSection = styled.section`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

// Seção técnica com padding ajustado
export const TechnicalSection = styled(FormSection)`
  padding-right: 4rem; // Voltando ao padding original
`;

// Grid técnico ajustado
export const TechnicalGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  align-items: start;
  width: 95%; // Voltando à largura original
`;

// Grupo de campos mais compacto
export const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
`;

// Título do campo
export const FieldLabel = styled.label`
  font-size: 1.125rem;
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 500;
`;

// Descrição abaixo do campo
export const FieldDescription = styled.span`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-top: 0.375rem;
`;

// Campo de entrada adaptado ao tema e com formatação de número
export const BaseInput = styled.input`
  height: 48px;
  width: 100%;
  padding: 0 1.25rem;
  border: 1px solid ${({ theme, error }) => 
    error ? theme.colors.error : theme.colors.border};
  border-radius: 8px;
  background: ${({ theme }) => theme.colors.background.main};
  color: ${({ theme }) => theme.colors.text.primary};
  font-size: 1.125rem;
  transition: all 0.2s;

  /* Remove arrows/spinners */
  &::-webkit-outer-spin-button,
  &::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }
  &[type=number] {
    -moz-appearance: textfield;
  }

  &::placeholder {
    font-size: 1rem;
    color: ${({ theme }) => theme.colors.text.placeholder};
  }

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary.light};
  }

  &:focus {
    border-color: ${({ theme }) => theme.colors.primary.main};
    outline: none;
    box-shadow: 0 0 0 2px ${({ theme }) => theme.colors.primary.light}20;
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;

// Select com mais espaço
export const Select = styled(BaseInput).attrs({ as: 'select' })`
  cursor: pointer;
  padding: 0 1.25rem;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  min-width: 250px; // Aumentado
  width: 100%; // Ocupa todo espaço disponível
`;

// Ações do formulário ao lado da demanda contratada
export const FormActions = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-top: 1.5rem; // Alinha com o campo de demanda
  grid-column: 1;
`;

// Botão de busca
export const SubmitButton = styled.button`
  height: 48px;
  width: 340px;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 8px;
  font-weight: 500;
  font-size: 1.125rem;
  transition: all 0.2s;

  &:hover {
    background: ${({ theme }) => theme.colors.primary.dark};
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;

export const ErrorMessage = styled.div`
  color: ${({ theme }) => theme.colors.error};
  background: ${({ theme }) => theme.colors.error}10;
  padding: 1rem;
  border-radius: 8px;
  margin: 1rem 0;
  font-size: 1rem;
`;

export const ViewControls = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

export const ViewButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.25rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : theme.colors.background.card};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  font-weight: 500;
  transition: all 0.2s ease;

  span {
    margin-left: 0.25rem; // Espaço entre ícone e texto
  }

  &:hover {
    background: ${({ $active, theme }) => 
      $active ? theme.colors.primary.dark : theme.colors.background.hover};
  }
`;

// Melhorando o estilo da tabela
export const TableContainer = styled.div`
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.shadows.md};
  overflow: hidden;
  border: 1px solid ${({ theme }) => theme.colors.border};
`;

export const Table = styled.table`
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;

  th, td {
    padding: 1rem 1.5rem;
    border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  }

  th {
    background: ${({ theme }) => theme.colors.background.secondary};
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.875rem;
    letter-spacing: 0.05em;
  }

  tr:hover td {
    background: ${({ theme }) => theme.colors.background.hover};
  }

  td {
    transition: background-color 0.2s;
  }
`;
