import React from 'react';
import styled from 'styled-components';
import { GridIcon, ListIcon, FilterIcon } from './icons';

const BuscaToolbar = ({ onToggleFiltros, onToggleView, viewMode, totalFornecedores, filtrosAtivos }) => {
  return (
    <Container>
      <ViewControls>
        <IconButton 
          onClick={() => onToggleView('grid')} 
          $active={viewMode === 'grid'}
          aria-label="Visualização em grade"
        >
          <GridIcon size={20} />
        </IconButton>
        <IconButton 
          onClick={() => onToggleView('list')} 
          $active={viewMode === 'list'}
          aria-label="Visualização em lista"
        >
          <ListIcon size={20} />
        </IconButton>
      </ViewControls>
      
      <FilterButton onClick={onToggleFiltros} $active={filtrosAtivos > 0}>
        <FilterIcon size={20} />
        <span>Filtros {filtrosAtivos > 0 && `(${filtrosAtivos})`}</span>
      </FilterButton>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  margin-bottom: 1rem;
`;

const ViewControls = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const IconButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : 'transparent'};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${({ $active, theme }) => 
      $active ? theme.colors.primary.dark : theme.colors.background.hover};
  }
`;

const FilterButton = styled(IconButton)`
  width: auto;
  padding: 0 1rem;
  gap: 0.5rem;

  span {
    font-size: 0.875rem;
  }
`;

export default BuscaToolbar;
