import React from 'react';
import styled from 'styled-components';
import { 
  SearchIcon,
  ChartIcon,
  FileIcon,
  GearIcon
} from './icons';

const Container = styled.nav`
  display: flex;
  gap: 0.5rem;
  padding: 1rem;
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background: ${({ $active }) => 
    $active ? '#1E88E5' : 'transparent'};
  color: ${({ $active }) => 
    $active ? '#FFFFFF' : '#212121'};
  border: none;
  cursor: pointer;
  border-radius: 4px;

  &:hover {
    background: ${({ $active }) => 
      $active ? '#1976D2' : '#f5f5f5'};
  }
`;

const TabNavigation = ({ activeTab = 'busca', onTabChange = () => {} }) => {
  const renderIcon = (Icon, size = 20) => React.createElement(Icon, { size });

  const tabs = [
    { id: 'busca', icon: SearchIcon, label: 'Buscar Fornecedores' },
    { id: 'economia', icon: ChartIcon, label: 'Análise de Economia' },
    { id: 'comparacao', icon: FileIcon, label: 'Comparar Fornecedores' },
    { id: 'configuracoes', icon: GearIcon, label: 'Configurações' }
  ];

  return (
    <Container>
      {tabs.map(({ id, icon, label }) => (
        <TabButton
          key={id}
          $active={activeTab === id}
          onClick={() => onTabChange(id)}
          type="button"
        >
          {renderIcon(icon)}
          <span>{label}</span>
        </TabButton>
      ))}
    </Container>
  );
};

export default React.memo(TabNavigation);
