import styled from 'styled-components';

export const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
  margin-left: -3.5rem;
`;

export const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
`;

export const FieldLabel = styled.label`
  font-size: 0.875rem;
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 500;
`;

export const FieldDescription = styled.span`
  font-size: 0.75rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-top: 0.25rem;
`;

export const BaseInput = styled.input`
  height: 38px;
  width: 340px;
  padding: 0 1rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: 6px;
  background: white;
  font-size: 0.875rem;

  &:focus {
    border-color: ${({ theme }) => theme.colors.primary.main};
    outline: none;
    box-shadow: 0 0 0 2px ${({ theme }) => theme.colors.primary.light}20;
  }
`;

export const Select = styled(BaseInput).attrs({ as: 'select' })``;

export const TechnicalGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 340px);
  gap: 1.5rem;
`;

export const FormLayout = styled.div`
  display: grid;
  grid-template-columns: 380px 1fr;
  gap: 2rem;
`;

export const FormSection = styled.section`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

export const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
`;

export const TabNavigation = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
`;

export const Tab = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.25rem;
  background: transparent;
  border: none;
  border-radius: 6px 6px 0 0;
  color: ${({ active, theme }) => 
    active ? theme.colors.primary.main : theme.colors.text.secondary};
  font-weight: 500;
  transition: all 0.2s;
  border-bottom: 2px solid ${({ active, theme }) => 
    active ? theme.colors.primary.main : 'transparent'};

  &:hover {
    color: ${({ theme }) => theme.colors.primary.main};
  }
`;

export const FormActions = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-top: 2rem;
`;

export const SubmitButton = styled.button`
  height: 38px;
  width: 340px;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 6px;
  font-weight: 500;
  transition: all 0.2s;

  &:hover {
    background: ${({ theme }) => theme.colors.primary.dark};
  }
`;
