import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';

const ConsumoForm = ({ onSubmit, consumo, setConsumo, buttonText = 'Buscar', error, setError }) => {
  const handleChange = (e) => {
    const value = e.target.value.replace(/[^\d]/g, '');
    setConsumo(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  // Formata o número com pontos para milhares
  const formatarNumero = (valor) => {
    if (!valor) return '';
    return valor.replace(/\B(?=(\d{3})+(?!\d))/g, ".");
  };

  return (
    <>
      <Form onSubmit={handleSubmit} role="search">
        <Label htmlFor="consumo-input">Consumo Mensal de Energia</Label>
        <Input
          id="consumo-input"
          type="text"
          value={formatarNumero(consumo)}
          onChange={handleChange}
          placeholder="Digite seu consumo em kWh"
          required
          aria-describedby="consumo-exemplo consumo-erro"
          aria-invalid={error ? 'true' : 'false'}
        />
      </Form>
      <ConsumoRange>
        <Small id="consumo-exemplo">Consumo: Min. 100 kWh - Máx. 100.000 kWh</Small>
        <ConsumoTip>
          Não sabe seu consumo? Veja os exemplos abaixo
        </ConsumoTip>
      </ConsumoRange>
      {error && <ErrorMessage id="consumo-erro">{error}</ErrorMessage>}
    </>
  );
};

ConsumoForm.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  consumo: PropTypes.string.isRequired,
  setConsumo: PropTypes.func.isRequired,
  buttonText: PropTypes.string,
  error: PropTypes.string,
  setError: PropTypes.func.isRequired
};

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Label = styled.label`
  color: ${({ theme }) => theme.colors.text.primary};
  font-size: 0.875rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const Input = styled.input`
  width: 100%;
  height: 42px;
  padding: 0 1rem;
  border: 2px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: 1rem;
  background: ${({ theme }) => theme.colors.background.alt};
  color: ${({ theme }) => theme.colors.text.primary};
  transition: all 0.2s ease;

  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.colors.primary.main};
    box-shadow: 0 0 0 3px ${({ theme }) => theme.colors.primary.main}20;
  }

  &::placeholder {
    color: ${({ theme }) => theme.colors.text.light};
  }
`;

const Small = styled.small`
  color: ${({ theme }) => theme.colors.text.secondary};
  font-size: ${({ theme }) => theme.typography.body2.fontSize};
`;

const ErrorMessage = styled.p`
  color: ${({ theme }) => theme.colors.error};
  font-size: ${({ theme }) => theme.typography.body2.fontSize};
  margin-top: ${({ theme }) => theme.spacing.xs};
  font-weight: 500;
`;

const ConsumoRange = styled.div`
  margin-top: ${({ theme }) => theme.spacing.xs};
`;

const ConsumoTip = styled.small`
  display: block;
  color: ${({ theme }) => theme.colors.text.light};
  font-size: ${({ theme }) => theme.typography.body2.fontSize};
  margin-top: ${({ theme }) => theme.spacing.xs};
`;

export default ConsumoForm;
