import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from './Button';
import api from '../services/api';

// Estilo do formulário principal
const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
  margin-left: -3.5rem;
`;

// Grupo de campos com espaçamento consistente
const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  margin-bottom: 1.5rem;
`;

// Título da seção
const SectionTitle = styled.h3`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.text.primary};
  margin-bottom: 1rem;
`;

// Input base para todos os campos
const BaseInput = styled.input`
  height: 38px;
  width: 340px;
  padding: 0 1rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: 6px;
  background: white;
`;

// Select estilizado
const Select = styled(BaseInput).attrs({ as: 'select' })``;

// Container dos dados técnicos
const TechnicalContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 340px);
  gap: 1.5rem;
  margin-top: 1rem;
`;

// Container do perfil de consumo
const ProfileContainer = styled.div`
  display: grid;
  grid-template-columns: 340px auto;
  gap: 1.5rem;
  align-items: start;
  margin-top: 1rem;
`;

// Grupo de horário
const TimeGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  input {
    width: 140px;
  }

  span {
    color: ${({ theme }) => theme.colors.text.secondary};
  }
`;

// Botão de busca alinhado com os campos
const SearchButton = styled.button`
  height: 38px;
  width: 340px;
  margin-top: 1.5rem;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 6px;
`;

const ClienteForm = () => {
  const [formData, setFormData] = useState({
    consumoMensal: '',
    demandaContratada: '',
    tensaoFornecimento: '',
    classeConsumidor: '',
    modalidadeTarifaria: '',
    diasFuncionamento: '',
    horarioFuncionamento: { inicio: '', fim: '' }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Permite busca com campos parcialmente preenchidos
    const filteredData = Object.fromEntries(
      Object.entries(formData).filter(([_, value]) => value !== '')
    );

    try {
      const response = await api.post('/buscar-fornecedores', filteredData);
      // Atualizar estado com os resultados
      setResultados(response.data);
    } catch (error) {
      console.error('Erro ao buscar fornecedores:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <Form onSubmit={handleSubmit}>
      {/* Dados de Consumo */}
      <FieldGroup>
        <SectionTitle>Dados de Consumo</SectionTitle>
        
        <FieldGroup>
          <FieldLabel>Consumo Mensal (kWh)</FieldLabel>
          <BaseInput
            type="number"
            name="consumoMensal"
            value={formData.consumoMensal}
            onChange={handleInputChange}
            placeholder="Ex: 30000"
          />
          <FieldDescription>Média dos últimos 12 meses</FieldDescription>
        </FieldGroup>

        <FieldGroup>
          <FieldLabel>Demanda Contratada (kW)</FieldLabel>
          <BaseInput
            type="number"
            name="demandaContratada"
            value={formData.demandaContratada}
            onChange={handleInputChange}
            placeholder="Ex: 100"
          />
          <FieldDescription>Potência contratada atual</FieldDescription>
        </FieldGroup>
      </FieldGroup>

      {/* Dados Técnicos */}
      <TechnicalContainer>
        <FieldGroup>
          <FieldLabel>Tensão de Fornecimento</FieldLabel>
          <Select
            name="tensaoFornecimento"
            value={formData.tensaoFornecimento}
            onChange={handleInputChange}
          >
            <option value="baixa">Baixa Tensão (BT)</option>
          </Select>
        </FieldGroup>

        <FieldGroup>
          <FieldLabel>Classe do Consumidor</FieldLabel>
          <Select
            name="classeConsumidor"
            value={formData.classeConsumidor}
            onChange={handleInputChange}
          >
            <option value="comercial">Comercial</option>
          </Select>
        </FieldGroup>

        <FieldGroup>
          <FieldLabel>Modalidade Tarifária</FieldLabel>
          <Select
            name="modalidadeTarifaria"
            value={formData.modalidadeTarifaria}
            onChange={handleInputChange}
          >
            <option value="convencional">Convencional</option>
          </Select>
        </FieldGroup>
      </TechnicalContainer>

      {/* Perfil de Consumo */}
      <ProfileContainer>
        <FieldGroup>
          <FieldLabel>Dias de Funcionamento</FieldLabel>
          <Select
            name="diasFuncionamento"
            value={formData.diasFuncionamento}
            onChange={handleInputChange}
          >
            <option value="5">Segunda a Sexta</option>
          </Select>
        </FieldGroup>

        <FieldGroup>
          <FieldLabel>Horário de Funcionamento</FieldLabel>
          <TimeGroup>
            <BaseInput
              type="time"
              name="horarioInicio"
              value={formData.horarioFuncionamento.inicio}
              onChange={handleInputChange}
            />
            <span>até</span>
            <BaseInput
              type="time"
              name="horarioFim"
              value={formData.horarioFuncionamento.fim}
              onChange={handleInputChange}
            />
          </TimeGroup>
        </FieldGroup>
      </ProfileContainer>

      <SearchButton type="submit">Buscar</SearchButton>
    </Form>
  );
};

export default ClienteForm;
