import React, { useState, useMemo } from 'react';
import { useQuery, gql } from '@apollo/client';
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components';
import ConsumoForm from './components/ConsumoForm';
import FiltrosOrdenacao from './components/FiltrosOrdenacao';
import FornecedorCard from './components/FornecedorCard';
import { fadeIn } from './styles/animations';
import { lightTheme, darkTheme } from './styles/theme';
import ThemeToggle from './components/ThemeToggle';
import { Input, Select, InputWrapper, SelectWrapper, Label } from './components/Form';
import { Button } from './components/Button';
import SidePanel from './components/SidePanel';
import { SearchIcon } from './components/icons';
import { BuscaGrid, ButtonContainer, ConsumoRange } from './components/Layout';
import FiltrosAvancados from './components/FiltrosAvancados';
import { useFiltrosAvancados } from './hooks/useFiltrosAvancados';
import BuscaToolbar from './components/BuscaToolbar';
import EconomiaAnalytics from './components/EconomiaAnalytics';
import TabNavigation from './components/TabNavigation';

const GET_FORNECEDORES = gql`
  query GetFornecedores($consumo: Float!) {
    fornecedoresPorConsumo(consumoMensal: $consumo) {
      id
      nome
      logo
      estadoOrigem
      custoKwh
      limiteMinKwh
      totalClientes
      avaliacaoMedia
      descricao
      certificacoes
    }
  }
`;

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    background: ${({ theme }) => theme.colors.background.main};
    color: ${({ theme }) => theme.colors.text.primary};
    transition: all 0.2s ease-in-out;
  }
`;

const Layout = styled.div`
  display: flex;
`;

const MainContent = styled.div`
  margin-left: ${({ $isPanelOpen }) => $isPanelOpen ? '200px' : '56px'};
  width: calc(100% - ${({ $isPanelOpen }) => $isPanelOpen ? '200px' : '56px'});
  transition: all 0.2s ease;
  background: ${({ theme }) => theme.colors.background.main};
  min-height: 100vh;
  padding: 0;
`;

const ContentArea = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const TopBar = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.75rem 1.5rem;
  background: ${({ theme }) => theme.colors.background.card};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  height: 56px;
`;

const CompanyHeader = styled.div`
  text-align: left;
  margin-bottom: 2rem;
`;

const CompanyName = styled.h1`
  font-size: 1.75rem;
  color: ${({ theme }) => 
    theme.type === 'dark' 
      ? theme.colors.primary.main 
      : theme.colors.primary.darker
  };
  margin: 0;
`;

const CompanyTagline = styled.p`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  margin: 0.5rem 0 0 0;
`;

const App = () => {
  const [consumo, setConsumo] = useState('');
  const [estado, setEstado] = useState('Todos');
  const [ordenacao, setOrdenacao] = useState('custoKwh_asc');
  const [buscar, setBuscar] = useState(false);
  const [isDarkTheme, setIsDarkTheme] = useState(() => {
    const saved = localStorage.getItem('theme');
    return saved === 'dark';
  });
  const [error, setError] = useState('');
  const [estados, setEstados] = useState(['SP', 'RJ', 'MG', 'ES', 'BA', 'PR', 'SC', 'RS', 'MS', 'MT', 'GO', 'DF', 'CE', 'RN', 'PE', 'PB', 'MA', 'PI', 'AL', 'SE', 'AM', 'PA', 'RO', 'AP', 'RR', 'AC']);
  const [isPanelOpen, setIsPanelOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('busca');
  const [viewMode, setViewMode] = useState('grid');
  const [filtrosAbertos, setFiltrosAbertos] = useState(false);

  const { loading, error: queryError, data } = useQuery(GET_FORNECEDORES, {
    variables: {
      consumo: parseFloat(consumo)
    },
    skip: !buscar || !consumo
  });

  const { 
    filtros, 
    filtrosSalvos, 
    handleChange: handleFiltroChange,
    salvarFiltro,
    limparFiltros,
    aplicarFiltroSalvo,
    aplicarFiltros
  } = useFiltrosAvancados();

  const handleSubmit = () => {
    const consumoNumerico = parseFloat(consumo.replace(/\D/g, ''));
    
    if (isNaN(consumoNumerico) || consumoNumerico <= 0) {
      setError('Por favor, insira um consumo v√°lido');
      return;
    }
    
    if (consumoNumerico > 100000) {
      setError('Consumo muito alto. Entre em contato com nosso suporte.');
      return;
    }

    setError('');
    setBuscar(true);
  };

  const handleLimparFiltros = () => {
    setEstado('Todos');
    setOrdenacao('custoKwh_asc');
  };

  const handleNovaBusca = () => {
    setConsumo('');
    setBuscar(false);
  };

  const fornecedoresFiltrados = React.useMemo(() => {
    if (!data?.fornecedoresPorConsumo) return [];
    
    let resultado = [...data.fornecedoresPorConsumo];
    
    // Aplicar filtros b√°sicos
    if (estado !== 'Todos') {
      resultado = resultado.filter(f => f.estadoOrigem === estado);
    }
    
    // Aplicar filtros avan√ßados
    resultado = aplicarFiltros(resultado);
    
    // Aplicar ordena√ß√£o
    const [criterio, ordem] = ordenacao.split('_');
    resultado.sort((a, b) => {
      return ordem === 'asc' 
        ? a[criterio] - b[criterio]
        : b[criterio] - a[criterio];
    });
    
    return resultado;
  }, [data, estado, ordenacao, aplicarFiltros]);

  const toggleTheme = () => {
    const newTheme = !isDarkTheme;
    setIsDarkTheme(newTheme);
    localStorage.setItem('theme', newTheme ? 'dark' : 'light');
  };

  const formatarNumero = (valor) => {
    if (!valor) return '';
    return valor.replace(/\B(?=(\d{3})+(?!\d))/g, ".");
  };

  const handleChange = (e) => {
    const value = e.target.value.replace(/[^\d]/g, '');
    setConsumo(value);
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'busca':
        return (
          <div>
            <BuscaContainer>
              <BuscaGrid>
                <div>
                  <InputWrapper>
                    <Label htmlFor="consumo-input">Consumo Mensal de Energia</Label>
                    <Input
                      id="consumo-input"
                      type="text"
                      value={formatarNumero(consumo)}
                      onChange={handleChange}
                      placeholder="Digite seu consumo em kWh"
                    />
                  </InputWrapper>
                  <ConsumoRange>
                    Consumo: Min. 100 kWh - M√°x. 100.000 kWh
                  </ConsumoRange>
                </div>
                
                <SelectWrapper>
                  <Label>Estado</Label>
                  <Select
                    value={estado}
                    onChange={(e) => setEstado(e.target.value)}
                    aria-label="Filtrar por estado"
                  >
                    <option value="Todos">Todos os estados</option>
                    {estados.map(uf => (
                      <option key={uf} value={uf}>{uf}</option>
                    ))}
                  </Select>
                </SelectWrapper>

                <SelectWrapper>
                  <Label>Ordenar por</Label>
                  <Select
                    value={ordenacao}
                    onChange={(e) => setOrdenacao(e.target.value)}
                    aria-label="Ordenar resultados"
                  >
                    <option value="custoKwh_asc">Menor Pre√ßo</option>
                    <option value="avaliacaoMedia_desc">Melhor Avalia√ß√£o</option>
                    <option value="totalClientes_desc">Mais Clientes</option>
                  </Select>
                </SelectWrapper>

                <ButtonContainer>
                  <Button 
                    type="submit"
                    onClick={handleSubmit}
                    aria-label="Buscar fornecedores"
                  >
                    <span><SearchIcon /></span>
                    <span>Buscar</span>
                  </Button>
                </ButtonContainer>
              </BuscaGrid>
            </BuscaContainer>
            
            <BuscaToolbar
              onToggleFiltros={() => setFiltrosAbertos(!filtrosAbertos)}
              onToggleView={setViewMode}
              viewMode={viewMode}
              totalFornecedores={fornecedoresFiltrados?.length || 0}
              filtrosAtivos={Object.keys(filtros).filter(k => filtros[k]).length}
            />

            {filtrosAbertos && (
              <FiltrosAvancados
                filtros={filtros}
                onChange={handleFiltroChange}
                onSalvar={salvarFiltro}
                onLimpar={limparFiltros}
                filtrosSalvos={filtrosSalvos}
                onAplicarFiltroSalvo={aplicarFiltroSalvo}
              />
            )}

            {loading && <Loading>Carregando fornecedores...</Loading>}
            
            {queryError && (
              <Error>
                <ErrorIcon>‚ö†Ô∏è</ErrorIcon>
                <span>Erro ao buscar fornecedores: {queryError.message}</span>
              </Error>
            )}
            
            {buscar && !loading && !queryError && (
              <ResultadosContainer>
                <ResultadoHeader>
                  <h2>Fornecedores Dispon√≠veis</h2>
                  <ResultadoCount>
                    {fornecedoresFiltrados.length} 
                    {fornecedoresFiltrados.length === 1 ? ' fornecedor' : ' fornecedores'} 
                    {estado !== 'Todos' ? ` em ${estado}` : ''}
                  </ResultadoCount>
                </ResultadoHeader>

                {fornecedoresFiltrados.length > 0 ? (
                  <ResultadosGrid>
                    {fornecedoresFiltrados.map(fornecedor => (
                      <FornecedorCard key={fornecedor.id} fornecedor={fornecedor} />
                    ))}
                  </ResultadosGrid>
                ) : (
                  <SemResultados>
                    <NoResultsIcon>üîç</NoResultsIcon>
                    <h3>Nenhum fornecedor encontrado</h3>
                    <p>
                      N√£o encontramos fornecedores {estado !== 'Todos' ? `em ${estado} ` : ''}
                      para o consumo de {parseInt(consumo).toLocaleString()} kWh.
                    </p>
                    <p>Tente:</p>
                    <ul>
                      <li>Selecionar outro estado</li>
                      <li>Ajustar o consumo mensal</li>
                      <li>Remover os filtros</li>
                    </ul>
                  </SemResultados>
                )}
              </ResultadosContainer>
            )}
          </div>
        );
      
      case 'economia':
        return (
          <EconomiaAnalytics
            consumoMensal={parseFloat(consumo)}
            fornecedorSelecionado={fornecedoresFiltrados?.[0] || null}
          />
        );
      
      default:
        return null;
    }
  };

  return (
    <ThemeProvider theme={isDarkTheme ? darkTheme : lightTheme}>
      <div>
        <GlobalStyle />
        <Layout>
          <SidePanel isOpen={isPanelOpen} togglePanel={() => setIsPanelOpen(!isPanelOpen)} />
          <MainContent $isPanelOpen={isPanelOpen}>
            <TopBar>
              <CompanyHeader>
                <CompanyName>Clarke Energia</CompanyName>
                <CompanyTagline>Economia e sustentabilidade para sua empresa</CompanyTagline>
              </CompanyHeader>
              <ThemeToggle isDark={isDarkTheme} onToggle={toggleTheme} />
            </TopBar>
            
            <ContentArea>
              <TabNavigation
                activeTab={activeTab}
                onTabChange={setActiveTab}
              />
              {renderContent()}
            </ContentArea>
          </MainContent>
        </Layout>
      </div>
    </ThemeProvider>
  );
};

const Container = styled.div`
  max-width: 1024px;
  margin: 0 auto;
  padding: 2rem;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: ${({ theme }) => theme.colors.text.primary};
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  font-size: 1.125rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const ResultadoHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;

  h2 {
    font-size: 1.5rem;
    color: ${({ theme }) => theme.colors.text.primary};
  }
`;

const ResultadosGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
  padding: 1rem 0;
`;

const ResultadosHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
`;

const Loading = styled.p`
  text-align: center;
  margin: 40px 0;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const Error = styled.p`
  text-align: center;
  color: red;
  margin: 40px 0;
`;

const BuscaContainer = styled.div`
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.shadows.sm};
  padding: 1.5rem;
  margin-bottom: 2rem;
`;

const ResultadosContainer = styled.div`
  margin-top: 2rem;
`;

const ResultadoCount = styled.span`
  color: ${({ theme }) => theme.colors.text.secondary};
  font-size: 0.9rem;
`;

const SemResultados = styled.div`
  text-align: center;
  padding: 48px 24px;
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: 12px;
  box-shadow: ${({ theme }) => theme.shadows.card};
  border: 1px solid ${({ theme }) => theme.colors.border};

  h3 {
    margin: 16px 0;
    color: ${({ theme }) => theme.colors.text.primary};
  }

  p {
    color: ${({ theme }) => theme.colors.text.secondary};
    margin-bottom: 16px;
  }

  ul {
    list-style: none;
    margin: 0;
    padding: 0;
    color: ${({ theme }) => theme.colors.text.secondary};

    li {
      margin: 8px 0;
      &:before {
        content: "‚Ä¢";
        color: ${({ theme }) => theme.colors.primary.main};
        margin-right: 8px;
      }
    }
  }
`;

const NoResultsIcon = styled.div`
  font-size: 48px;
`;

const ErrorIcon = styled.span`
  margin-right: 8px;
`;

const Small = styled.small`
  font-size: 0.8rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

export default App;
