import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from './Button';

const ClienteForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    // Dados de Consumo
    consumoMensal: '',
    demandaContratada: '',
    
    // Dados Técnicos
    tensaoFornecimento: 'baixa',
    classeConsumidor: 'comercial',
    modalidadeTarifaria: 'convencional',
    
    // Dados de Faturamento Atual
    custoMedioMensal: '',
    valorUltimaFatura: '',
    distribuidoraAtual: '',
    
    // Perfil de Consumo
    horarioPonta: false,
    percentualPonta: '',
    diasFuncionamento: '5',
    horarioFuncionamento: {
      inicio: '08:00',
      fim: '18:00'
    },
    
    // Dados Adicionais
    possuiGeracao: false,
    tipoGeracao: '',
    potenciaInstalada: '',
    area: '',
    numeroFuncionarios: '',
  });

  const handleChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <FormContainer>
      <Form onSubmit={() => onSubmit(formData)}>
        <TopSection>
          {/* Dados de Consumo */}
          <FormSection>
            <SectionTitle>Dados de Consumo</SectionTitle>
            <FormGroup>
              <Label>Consumo Mensal (kWh)</Label>
              <Input
                type="number"
                name="consumoMensal"
                value={formData.consumoMensal}
                onChange={(e) => handleChange('consumoMensal', e.target.value)}
                placeholder="Ex: 30000"
              />
              <HelpText>Média dos últimos 12 meses</HelpText>
            </FormGroup>

            <FormGroup>
              <Label>Demanda Contratada (kW)</Label>
              <Input
                type="number"
                name="demandaContratada"
                value={formData.demandaContratada}
                onChange={(e) => handleChange('demandaContratada', e.target.value)}
                placeholder="Ex: 100"
              />
              <HelpText>Potência contratada atual</HelpText>
            </FormGroup>
          </FormSection>

          {/* Dados Técnicos */}
          <FormSection>
            <SectionTitle>Dados Técnicos</SectionTitle>
            <TecnicosGrid>
              <FormGroup>
                <Label>Tensão de Fornecimento</Label>
                <Select
                  name="tensaoFornecimento"
                  value={formData.tensaoFornecimento}
                  onChange={(e) => handleChange('tensaoFornecimento', e.target.value)}
                >
                  <option value="baixa">Baixa Tensão (BT)</option>
                  <option value="media">Média Tensão (MT)</option>
                  <option value="alta">Alta Tensão (AT)</option>
                </Select>
              </FormGroup>

              <FormGroup>
                <Label>Classe do Consumidor</Label>
                <Select
                  name="classeConsumidor"
                  value={formData.classeConsumidor}
                  onChange={(e) => handleChange('classeConsumidor', e.target.value)}
                >
                  <option value="comercial">Comercial</option>
                  <option value="industrial">Industrial</option>
                  <option value="residencial">Residencial</option>
                </Select>
              </FormGroup>

              <FormGroup>
                <Label>Modalidade Tarifária</Label>
                <Select
                  name="modalidadeTarifaria"
                  value={formData.modalidadeTarifaria}
                  onChange={(e) => handleChange('modalidadeTarifaria', e.target.value)}
                >
                  <option value="convencional">Convencional</option>
                  <option value="branca">Tarifa Branca</option>
                  <option value="verde">Tarifa Verde</option>
                  <option value="azul">Tarifa Azul</option>
                </Select>
              </FormGroup>
            </TecnicosGrid>
          </FormSection>
        </TopSection>

        {/* Perfil de Consumo */}
        <FormSection>
          <SectionTitle>Perfil de Consumo</SectionTitle>
          <ConsumoGrid>
            <FormGroup>
              <Label>Dias de Funcionamento</Label>
              <Select
                name="diasFuncionamento"
                value={formData.diasFuncionamento}
                onChange={(e) => handleChange('diasFuncionamento', e.target.value)}
              >
                <option value="5">Segunda a Sexta</option>
                <option value="6">Segunda a Sábado</option>
                <option value="7">Todos os dias</option>
              </Select>
            </FormGroup>

            <FormGroup>
              <Label>Horário de Funcionamento</Label>
              <TimeContainer>
                <TimeInput
                  name="horarioFuncionamento.inicio"
                  value={formData.horarioFuncionamento.inicio}
                  onChange={(e) => handleChange('horarioFuncionamento', {
                    ...formData.horarioFuncionamento,
                    inicio: e.target.value
                  })}
                />
                <TimeSpan>até</TimeSpan>
                <TimeInput
                  name="horarioFuncionamento.fim"
                  value={formData.horarioFuncionamento.fim}
                  onChange={(e) => handleChange('horarioFuncionamento', {
                    ...formData.horarioFuncionamento,
                    fim: e.target.value
                  })}
                />
              </TimeContainer>
            </FormGroup>

            <FormGroup>
              <SwitchContainer>
                <SwitchLabel>Consumo em Horário de Ponta?</SwitchLabel>
                <Switch
                  type="checkbox"
                  name="horarioPonta"
                  checked={formData.horarioPonta}
                  onChange={(e) => handleChange('horarioPonta', e.target.checked)}
                />
              </SwitchContainer>
            </FormGroup>
          </ConsumoGrid>
        </FormSection>

        <FormActions>
          <Button type="submit">Buscar Fornecedores</Button>
        </FormActions>
      </Form>
    </FormContainer>
  );
};

