import React from 'react';
import { BsFilter, BsSave, BsTrash } from 'react-icons/bs';
import { Input, Select, InputWrapper } from '../Form';
import * as S from './styles';

const FiltrosAvancados = ({ 
  filtros, 
  onChange, 
  onSalvar, 
  onLimpar,
  filtrosSalvos,
  onAplicarFiltroSalvo 
}) => {
  const [isOpen, setIsOpen] = React.useState(false);
  const [nomeFiltro, setNomeFiltro] = React.useState('');

  return (
    <S.Container>
      <S.Header>
        <S.ToggleButton onClick={() => setIsOpen(!isOpen)}>
          <span><BsFilter /></span>
          <span>Filtros Avançados</span>
        </S.ToggleButton>
        {isOpen && (
          <S.HeaderActions>
            <S.SaveContainer>
              <Input
                placeholder="Nome do filtro"
                value={nomeFiltro}
                onChange={(e) => setNomeFiltro(e.target.value)}
              />
              <S.Button onClick={() => {
                onSalvar(nomeFiltro, filtros);
                setNomeFiltro('');
              }}>
                <BsSave /> Salvar
              </S.Button>
            </S.SaveContainer>
            <S.Button onClick={onLimpar}>
              <BsTrash /> Limpar
            </S.Button>
          </S.HeaderActions>
        )}
      </S.Header>

      <S.Content
        initial={false}
        animate={{ height: isOpen ? 'auto' : 0 }}
        transition={{ duration: 0.3 }}
      >
        <S.Grid>
          <InputWrapper>
            <label>Economia Mínima (%)</label>
            <Input
              type="number"
              value={filtros.economiaMin}
              onChange={(e) => onChange('economiaMin', e.target.value)}
              placeholder="Ex: 10"
            />
          </InputWrapper>

          <InputWrapper>
            <label>Custo Máximo (kWh)</label>
            <Input
              type="number"
              value={filtros.custoMaximo}
              onChange={(e) => onChange('custoMaximo', e.target.value)}
              placeholder="Ex: 0.80"
              step="0.01"
            />
          </InputWrapper>

          <InputWrapper>
            <label>Certificações</label>
            <Select
              multiple
              value={filtros.certificacoes}
              onChange={(e) => onChange('certificacoes', 
                Array.from(e.target.selectedOptions, o => o.value)
              )}
            >
              <option value="ISO 9001">ISO 9001</option>
              <option value="ISO 14001">ISO 14001</option>
              <option value="Energia Verde">Energia Verde</option>
              <option value="Selo Solar">Selo Solar</option>
            </Select>
          </InputWrapper>

          <InputWrapper>
            <label>Tipo de Energia</label>
            <Select
              value={filtros.tipoEnergia}
              onChange={(e) => onChange('tipoEnergia', e.target.value)}
            >
              <option value="">Todos</option>
              <option value="solar">Solar</option>
              <option value="eolica">Eólica</option>
              <option value="biomassa">Biomassa</option>
            </Select>
          </InputWrapper>

          <InputWrapper>
            <label>Avaliação Mínima</label>
            <Select
              value={filtros.avaliacaoMin}
              onChange={(e) => onChange('avaliacaoMin', e.target.value)}
            >
              <option value="">Qualquer</option>
              <option value="3">3+ estrelas</option>
              <option value="4">4+ estrelas</option>
              <option value="4.5">4.5+ estrelas</option>
            </Select>
          </InputWrapper>

          <InputWrapper>
            <label>Total de Clientes (mínimo)</label>
            <Input
              type="number"
              value={filtros.clientesMin}
              onChange={(e) => onChange('clientesMin', e.target.value)}
              placeholder="Ex: 1000"
            />
          </InputWrapper>
        </S.Grid>

        {filtrosSalvos.length > 0 && (
          <S.FiltrosSalvosSection>
            <S.SectionTitle>Filtros Salvos</S.SectionTitle>
            <S.FiltrosList>
              {filtrosSalvos.map(filtro => (
                <S.FiltroCard 
                  key={filtro.id}
                  onClick={() => onAplicarFiltroSalvo(filtro)}
                >
                  <S.FiltroNome>{filtro.nome}</S.FiltroNome>
                  <S.FiltroData>
                    {new Date(filtro.dataCriacao).toLocaleDateString()}
                  </S.FiltroData>
                </S.FiltroCard>
              ))}
            </S.FiltrosList>
          </S.FiltrosSalvosSection>
        )}
      </S.Content>
    </S.Container>
  );
};

export default FiltrosAvancados;
