const theme = {
  colors: {
    background: {
      light: '#F8FAFC', // Fundo claro
      dark: '#1E293B',  // Fundo escuro
      card: '#FFFFFF',   // Fundo do cartão
      disabled: '#E2E8F0' // Fundo desabilitado
    },
