import React from 'react';
import { BsListUl, BsGrid3X3Gap } from 'react-icons/bs';
import styled from 'styled-components';

const ViewModeButtons = ({ viewMode, onViewChange }) => {
  return (
    <ViewControls>
      <ViewButton 
        onClick={() => onViewChange('list')}
        $active={viewMode === 'list'}
      >
        <BsListUl size={16} />
        <span>Lista</span>
      </ViewButton>
      <ViewButton 
        onClick={() => onViewChange('grid')}
        $active={viewMode === 'grid'}
      >
        <BsGrid3X3Gap size={16} />
        <span>Grid</span>
      </ViewButton>
    </ViewControls>
  );
};

const ViewControls = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

const ViewButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.25rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : theme.colors.background.card};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  font-weight: 500;
  transition: all 0.2s ease;
  cursor: pointer;

  &:hover {
    background: ${({ $active, theme }) => 
      $active ? theme.colors.primary.dark : theme.colors.background.hover};
  }
`;

export default ViewModeButtons;
