import styled from 'styled-components';

export const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
  margin-left: -3.5rem;
`;

// Container principal dos campos
export const FormLayout = styled.div`
  display: grid;
  grid-template-columns: 340px 1fr; // Primeira coluna fixa, segunda flexível
  gap: 3rem;
  margin-top: 2rem;
`;

// Título das seções
export const SectionTitle = styled.h2`
  font-size: 1.125rem;
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 600;
  margin-bottom: 1.5rem;
`;

// Seção de consumo (2 campos verticais)
export const FormSection = styled.section`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

// Grid para campos técnicos (3 campos horizontais)
export const TechnicalGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
`;

// Grupo de campos com label e descrição
export const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
`;

// Título do campo
export const FieldLabel = styled.label`
  font-size: 1rem; // Aumentado de 0.875rem
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 500;
`;

// Descrição abaixo do campo
export const FieldDescription = styled.span`
  font-size: 0.875rem; // Aumentado de 0.75rem
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-top: 0.25rem;
`;

// Campo de entrada base
export const BaseInput = styled.input`
  height: 42px; // Aumentado de 38px
  width: 100%; // Ajusta ao container
  padding: 0 1rem;
  border: 1px solid ${({ theme, error }) => 
    error ? theme.colors.error : theme.colors.border};
  border-radius: 6px;
  background: white;
  font-size: 1rem; // Aumentado de 0.875rem
  transition: all 0.2s;

  &::placeholder {
    font-size: 0.875rem;
    color: ${({ theme }) => theme.colors.text.placeholder};
  }

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary.light};
  }

  &:focus {
    border-color: ${({ theme }) => theme.colors.primary.main};
    outline: none;
    box-shadow: 0 0 0 2px ${({ theme }) => theme.colors.primary.light}20;
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;

// Select estilizado
export const Select = styled(BaseInput).attrs({ as: 'select' })`
  cursor: pointer;
  padding-right: 2rem;
  background-image: url("data:image/svg+xml,...");
  background-repeat: no-repeat;
  background-position: right 0.75rem center;
`;

// Ações do formulário
export const FormActions = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-top: 2rem;
  grid-column: 1 / -1; // Ocupa todas as colunas
`;

// Botão de busca
export const SubmitButton = styled.button`
  height: 42px; // Aumentado para combinar com os inputs
  width: 340px;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 6px;
  font-weight: 500;
  font-size: 1rem; // Aumentado
  transition: all 0.2s;

  &:hover {
    background: ${({ theme }) => theme.colors.primary.dark};
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;
