import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from './Button';
import api from '../services/api';

// Componentes base simplificados
const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;
`;

const Input = styled.input`
  height: 38px;
  padding: 0 1rem;
  background: ${({ theme }) => theme.colors.background.main};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: 6px;
  color: ${({ theme }) => theme.colors.text.primary};
  font-size: 0.875rem;
  width: 340px; // Largura padrão para todos os campos
`;

const Select = styled(Input).attrs({ as: 'select' })`
  cursor: pointer;
  appearance: none;
  background-position: right 1rem center;
  padding-right: 2.5rem;
`;

// Layout principal
const MainSection = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

// Seção de dados técnicos
const TechnicalGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 340px);
  gap: 2rem;
  margin-top: 2rem;
`;

// Seção de perfil de consumo
const ProfileGrid = styled.div`
  display: grid;
  grid-template-columns: 340px 340px;
  gap: 2rem;
  margin-top: 2rem;
`;

const SectionTitle = styled.h2`
  font-size: 1rem;
  font-weight: 600;
  margin: 2rem 0 1rem 0;
  color: ${({ theme }) => theme.colors.text.primary};
`;

const SubmitButton = styled.button`
  height: 38px;
  padding: 0 2rem;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 6px;
  font-weight: 500;
  cursor: pointer;
  margin-top: 2rem;
`;

const Label = styled.label`
  font-size: 0.875rem;
  color: ${({ theme }) => theme.colors.text.primary};
`;

const HelpText = styled.small`
  font-size: 0.75rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const ClienteForm = () => {
  const [formData, setFormData] = useState({
    consumoMensal: '',
    demandaContratada: '',
    tensaoFornecimento: '',
    classeConsumidor: '',
    modalidadeTarifaria: '',
    diasFuncionamento: '',
    horarioFuncionamento: { inicio: '', fim: '' }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Permite busca com campos parcialmente preenchidos
    const filteredData = Object.fromEntries(
      Object.entries(formData).filter(([_, value]) => value !== '')
    );

    try {
      const response = await api.post('/buscar-fornecedores', filteredData);
      // Atualizar estado com os resultados
      setResultados(response.data);
    } catch (error) {
      console.error('Erro ao buscar fornecedores:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <Form onSubmit={handleSubmit}>
      <MainSection>
        <SectionTitle>Dados de Consumo</SectionTitle>
        <FormGroup>
          <Label>Consumo Mensal (kWh)</Label>
          <Input
            type="number"
            name="consumoMensal"
            value={formData.consumoMensal}
            onChange={handleInputChange}
            placeholder="Ex: 30000"
          />
          <HelpText>Média dos últimos 12 meses</HelpText>
        </FormGroup>
        <FormGroup>
          <Label>Demanda Contratada (kW)</Label>
          <Input
            type="number"
            name="demandaContratada"
            value={formData.demandaContratada}
            onChange={handleInputChange}
            placeholder="Ex: 100"
          />
          <HelpText>Potência contratada atual</HelpText>
        </FormGroup>
      </MainSection>

      <TechnicalGrid>
        <FormGroup>
          <Label>Tensão de Fornecimento</Label>
          <Select
            name="tensaoFornecimento"
            value={formData.tensaoFornecimento}
            onChange={handleInputChange}
          >
            <option value="baixa">Baixa Tensão (BT)</option>
            <option value="media">Média Tensão (MT)</option>
            <option value="alta">Alta Tensão (AT)</option>
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Classe do Consumidor</Label>
          <Select
            name="classeConsumidor"
            value={formData.classeConsumidor}
            onChange={handleInputChange}
          >
            <option value="comercial">Comercial</option>
            <option value="industrial">Industrial</option>
            <option value="residencial">Residencial</option>
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Modalidade Tarifária</Label>
          <Select
            name="modalidadeTarifaria"
            value={formData.modalidadeTarifaria}
            onChange={handleInputChange}
          >
            <option value="convencional">Convencional</option>
            <option value="branca">Tarifa Branca</option>
            <option value="verde">Tarifa Verde</option>
            <option value="azul">Tarifa Azul</option>
          </Select>
        </FormGroup>
      </TechnicalGrid>

      <ProfileGrid>
        <FormGroup>
          <Label>Dias de Funcionamento</Label>
          <Select
            name="diasFuncionamento"
            value={formData.diasFuncionamento}
            onChange={handleInputChange}
          >
            <option value="5">Segunda a Sexta</option>
            <option value="6">Segunda a Sábado</option>
            <option value="7">Todos os dias</option>
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Horário de Funcionamento</Label>
          <TimeContainer>
            <TimeInput
              type="time"
              name="horarioInicio"
              value={formData.horarioFuncionamento.inicio}
              onChange={handleInputChange}
            />
            <TimeSpan>até</TimeSpan>
            <TimeInput
              type="time"
              name="horarioFim"
              value={formData.horarioFuncionamento.fim}
              onChange={handleInputChange}
            />
          </TimeContainer>
        </FormGroup>
      </ProfileGrid>

      <SubmitButton 
        type="submit" 
        disabled={Object.values(formData).every(value => value === '')}
      >
        Buscar
      </SubmitButton>
    </Form>
  );
};

// Styled Components Otimizados
const Form = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  background: ${({ theme }) => theme.type === 'dark' 
    ? theme.colors.background.dark 
    : theme.colors.background.light};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  overflow-x: hidden;
`;

const TimeContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  width: 100%;
  height: 32px;
`;

const TimeSpan = styled.span`
  color: ${({ theme }) => theme.colors.text.secondary};
  padding: 0 0.5rem;
`;

const TimeInput = styled(Input)`
  width: 120px;
  height: 32px;
`;

// Adicionar tooltips informativos
const InfoTooltip = styled.div`
  position: absolute;
  top: calc(100% + 5px);
  left: 0;
  background: ${({ theme }) => theme.colors.background.tooltip};
  padding: 0.75rem 1rem;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: 0.875rem;
  color: ${({ theme }) => theme.colors.text.primary};
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  width: 250px;
  z-index: 10;
  opacity: 0;
  visibility: hidden;
  transition: all 0.2s ease;

  ${({ visible }) => visible && `
    opacity: 1;
    visibility: visible;
  `}
`;

// Adicionar informações contextuais no componente
const ConsumptionInfo = () => (
  <div className="section-info">
    Média mensal: {formData.consumoMensal ? 
      `${Number(formData.consumoMensal).toLocaleString('pt-BR')} kWh` : 
      'Não informado'
    }
  </div>
);

