import React from 'react';
import styled from 'styled-components';
import { 
  BsSearch, 
  BsBarChart, 
  BsGear, 
  BsFileText 
} from 'react-icons/bs';

const tabs = [
  { id: 'busca', icon: BsSearch, label: 'Buscar Fornecedores' },
  { id: 'economia', icon: BsBarChart, label: 'Análise de Economia' },
  { id: 'comparacao', icon: BsFileText, label: 'Comparar Fornecedores' },
  { id: 'configuracoes', icon: BsGear, label: 'Configurações' }
];

const IconWrapper = styled.span`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  
  svg {
    font-size: 1.2rem;
  }
`;

const TabNavigation = ({ activeTab, onTabChange }) => (
  <Container>
    {tabs.map(tab => {
      const TabIcon = tab.icon;
      return (
        <TabButton
          key={tab.id}
          $active={activeTab === tab.id}
          onClick={() => onTabChange(tab.id)}
        >
          <IconWrapper>
            <TabIcon />
          </IconWrapper>
          <span>{tab.label}</span>
        </TabButton>
      );
    })}
  </Container>
);

const Container = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 2rem;
  background: ${({ theme }) => theme.colors.background.card};
  padding: 0.5rem;
  border-radius: ${({ theme }) => theme.borderRadius.lg};
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  background: ${({ $active, theme }) => 
    $active ? theme.colors.primary.main : 'transparent'};
  color: ${({ $active, theme }) => 
    $active ? theme.colors.text.inverse : theme.colors.text.primary};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${({ $active, theme }) => 
      $active ? theme.colors.primary.main : theme.colors.background.hover};
  }
`;

export default TabNavigation;
