import React, { useState } from 'react';
import styled from 'styled-components';
import { Button } from './Button';

const ClienteForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    // Dados de Consumo
    consumoMensal: '',
    demandaContratada: '',
    
    // Dados TÃ©cnicos
    tensaoFornecimento: 'baixa',
    classeConsumidor: 'comercial',
    modalidadeTarifaria: 'convencional',
    
    // Dados de Faturamento Atual
    custoMedioMensal: '',
    valorUltimaFatura: '',
    distribuidoraAtual: '',
    
    // Perfil de Consumo
    horarioPonta: false,
    percentualPonta: '',
    diasFuncionamento: '5',
    horarioFuncionamento: {
      inicio: '08:00',
      fim: '18:00'
    },
    
    // Dados Adicionais
    possuiGeracao: false,
    tipoGeracao: '',
    potenciaInstalada: '',
    area: '',
    numeroFuncionarios: '',
  });

  const handleChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <FormContainer>
      <FormSection>
        <SectionTitle>Dados de Consumo</SectionTitle>
        <FormGrid>
          <FormField>
            <Label>Consumo Mensal (kWh)</Label>
            <Input
              type="number"
              value={formData.consumoMensal}
              onChange={(e) => handleChange('consumoMensal', e.target.value)}
              placeholder="Ex: 30000"
            />
            <HelpText>MÃ©dia dos Ãºltimos 12 meses</HelpText>
          </FormField>

          <FormField>
            <Label>Demanda Contratada (kW)</Label>
            <Input
              type="number"
              value={formData.demandaContratada}
              onChange={(e) => handleChange('demandaContratada', e.target.value)}
              placeholder="Ex: 100"
            />
            <HelpText>PotÃªncia contratada atual</HelpText>
          </FormField>
        </FormGrid>
      </FormSection>

      <FormSection>
        <SectionTitle>Dados TÃ©cnicos</SectionTitle>
        <FormGrid>
          <FormField>
            <Label>TensÃ£o de Fornecimento</Label>
            <Select
              value={formData.tensaoFornecimento}
              onChange={(e) => handleChange('tensaoFornecimento', e.target.value)}
            >
              <option value="baixa">Baixa TensÃ£o (BT)</option>
              <option value="media">MÃ©dia TensÃ£o (MT)</option>
              <option value="alta">Alta TensÃ£o (AT)</option>
            </Select>
          </FormField>

          <FormField>
            <Label>Classe do Consumidor</Label>
            <Select
              value={formData.classeConsumidor}
              onChange={(e) => handleChange('classeConsumidor', e.target.value)}
            >
              <option value="comercial">Comercial</option>
              <option value="industrial">Industrial</option>
              <option value="residencial">Residencial</option>
            </Select>
          </FormField>

          <FormField>
            <Label>Modalidade TarifÃ¡ria</Label>
            <Select
              value={formData.modalidadeTarifaria}
              onChange={(e) => handleChange('modalidadeTarifaria', e.target.value)}
            >
              <option value="convencional">Convencional</option>
              <option value="branca">Tarifa Branca</option>
              <option value="verde">Tarifa Verde</option>
              <option value="azul">Tarifa Azul</option>
            </Select>
          </FormField>
        </FormGrid>
      </FormSection>

      <FormSection>
        <SectionTitle>Perfil de Consumo</SectionTitle>
        <FormGrid>
          <FormField>
            <Label>Consumo em HorÃ¡rio de Ponta?</Label>
            <SwitchContainer>
              <Switch
                type="checkbox"
                checked={formData.horarioPonta}
                onChange={(e) => handleChange('horarioPonta', e.target.checked)}
              />
              <SwitchLabel>
                {formData.horarioPonta ? 'Sim' : 'NÃ£o'}
              </SwitchLabel>
            </SwitchContainer>
          </FormField>

          {formData.horarioPonta && (
            <FormField>
              <Label>% Consumo na Ponta</Label>
              <Input
                type="number"
                value={formData.percentualPonta}
                onChange={(e) => handleChange('percentualPonta', e.target.value)}
                placeholder="Ex: 30"
              />
              <HelpText>Percentual do consumo entre 18h e 21h</HelpText>
            </FormField>
          )}

          <FormField>
            <Label>Dias de Funcionamento</Label>
            <Select
              value={formData.diasFuncionamento}
              onChange={(e) => handleChange('diasFuncionamento', e.target.value)}
            >
              <option value="5">Segunda a Sexta</option>
              <option value="6">Segunda a SÃ¡bado</option>
              <option value="7">Todos os dias</option>
            </Select>
          </FormField>

          <FormField>
            <Label>HorÃ¡rio de Funcionamento</Label>
            <TimeContainer>
              <TimeInput
                type="time"
                value={formData.horarioFuncionamento.inicio}
                onChange={(e) => handleChange('horarioFuncionamento', {
                  ...formData.horarioFuncionamento,
                  inicio: e.target.value
                })}
              />
              <span>atÃ©</span>
              <TimeInput
                type="time"
                value={formData.horarioFuncionamento.fim}
                onChange={(e) => handleChange('horarioFuncionamento', {
                  ...formData.horarioFuncionamento,
                  fim: e.target.value
                })}
              />
            </TimeContainer>
          </FormField>
        </FormGrid>
      </FormSection>

      <FormActions>
        <Button onClick={() => onSubmit(formData)}>
          Buscar Fornecedores
        </Button>
      </FormActions>
    </FormContainer>
  );
};

// Estilos
const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  padding: 1.5rem;
  background: ${({ theme }) => theme.colors.background.card};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.shadows.sm};
`;

// ... (outros estilos do componente)

export default ClienteForm;
