import styled from 'styled-components';

export const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 2rem;
  margin-left: -3.5rem;
`;

// Container principal dos campos - Aumentado o gap entre as seções
export const FormLayout = styled.div`
  display: grid;
  grid-template-columns: 340px minmax(800px, 1fr);
  gap: 6rem; // Aumentado de 3rem para 6rem
  margin-top: 2rem;
`;

// Título das seções com linha verde
export const SectionTitle = styled.h2`
  font-size: 1.25rem; // Aumentado
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 600;
  margin-bottom: 2rem;
  padding-bottom: 0.75rem;
  border-bottom: 2px solid ${({ theme }) => theme.colors.primary.main};
  width: 100%;
`;

// Seção de consumo
export const FormSection = styled.section`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

// Seção técnica com padding à esquerda
export const TechnicalSection = styled(FormSection)`
  padding-left: 2rem; // Adiciona espaço extra à esquerda
  border-left: 1px solid ${({ theme }) => theme.colors.border}; // Linha separadora opcional
`;

// Grid para campos técnicos
export const TechnicalGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr); // Distribui igualmente
  gap: 2rem;
  align-items: start;
`;

// Grupo de campos com label e descrição
export const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

// Título do campo
export const FieldLabel = styled.label`
  font-size: 1.125rem; // Aumentado
  color: ${({ theme }) => theme.colors.text.primary};
  font-weight: 500;
`;

// Descrição abaixo do campo
export const FieldDescription = styled.span`
  font-size: 1rem; // Aumentado
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-top: 0.375rem;
`;

// Campo de entrada base
export const BaseInput = styled.input`
  height: 48px; // Aumentado
  width: 100%;
  padding: 0 1.25rem;
  border: 1px solid ${({ theme, error }) => 
    error ? theme.colors.error : theme.colors.border};
  border-radius: 8px; // Aumentado para combinar com o tamanho maior
  background: white;
  font-size: 1.125rem; // Aumentado
  transition: all 0.2s;

  &::placeholder {
    font-size: 1rem;
    color: ${({ theme }) => theme.colors.text.placeholder};
  }

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary.light};
  }

  &:focus {
    border-color: ${({ theme }) => theme.colors.primary.main};
    outline: none;
    box-shadow: 0 0 0 2px ${({ theme }) => theme.colors.primary.light}20;
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;

// Select estilizado
export const Select = styled(BaseInput).attrs({ as: 'select' })`
  cursor: pointer;
  padding-right: 2.5rem; // Aumentado para o ícone
  background-image: url("data:image/svg+xml,...");
  background-repeat: no-repeat;
  background-position: right 1rem center;
  background-size: 1.25rem; // Aumentado
`;

// Ações do formulário
export const FormActions = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-top: 3rem; // Aumentado
  grid-column: 1 / -1;
`;

// Botão de busca
export const SubmitButton = styled.button`
  height: 48px; // Aumentado
  width: 340px;
  background: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  border-radius: 8px; // Aumentado
  font-weight: 500;
  font-size: 1.125rem; // Aumentado
  transition: all 0.2s;

  &:hover {
    background: ${({ theme }) => theme.colors.primary.dark};
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    background: ${({ theme }) => theme.colors.background.disabled};
    cursor: not-allowed;
  }
`;
